  CONSTRAINT `products_chk_2` CHECK ((`stock_quantity` >= 0)),
  CONSTRAINT `products_chk_3` CHECK (((`gst` >= 0) and (`gst` <= 100))),
  CONSTRAINT `products_chk_4` CHECK (((`c_gst` >= 0) and (`c_gst` <= 100))),
  CONSTRAINT `products_chk_5` CHECK (((`s_gst` >= 0) and (`s_gst` <= 100))),
  CONSTRAINT `products_chk_6` CHECK (((`gst` >= 0) and (`gst` <= 300))),
  CONSTRAINT `products_chk_7` CHECK (((`c_gst` >= 0) and (`c_gst` <= 300))),
  CONSTRAINT `products_chk_8` CHECK (((`s_gst` >= 0) and (`s_gst` <= 300))),
  CONSTRAINT `products_chk_9` CHECK ((`discount` between 0 and 100))
) ENGINE=InnoDB AUTO_INCREMENT=79 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;

